package com.twjitm.db.common;/** * @author EGLS0807 - [Created on 2018-08-26 12:13] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */public interface JsonSerializer {    /**     * 序列化     * 将obj对象中的相关数据取出，串行化为json字符串     * @return     */    public String serialize();    /**     * 反序列化     * 将json字符串里的信息抽取，反馈到对象中     * @param pack     */    public void deserialize(String pack);}