package com.twjitm.db.spring;/** * spring 异步任务回调 * * @author EGLS0807 - [Created on 2018-09-03 16:50] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */public interface AsyncTaskHandler<T> {    /**     * 执行成功     *     * @param result     */    public void success(T result);    /**     * 执行失败     * @param exception     */    public void error(RuntimeException exception);}